function equal_tables(a, b);
if #a ~= #b then;
return false;
else;
for k, v in pairs(a) do;
if type(v) ~= type(b[k]) then;
return false;
elseif type(v) == "table" then;
if not equal_tables(v, b[k]) then;
return false;
end;
else;
if v ~= b[k] then;
return false;
end;
end;
end;
return true;
end;
